import React, { useState } from 'react';
import { Form, Button, Card } from 'react-bootstrap';
import { ValidateEmail } from './ValidateEmail';

const MyForm = () => {

    const [name, setName] = useState("");
    const [lastname, setLastname] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    
    function handleSubmit(e){
        e.preventDefault();
        if(validateName(name) === true && validateName(lastname) === true && ValidateEmail(email) === true && validatePhoneNumber(phoneNumber) === true){
            fetch(
                "http://127.0.0.1:5000/user",
                {
                    method: "POST",
                    body: {
                        "name": JSON.stringify(name),
                        "lastname": JSON.stringify(lastname),
                        "email": JSON.stringify(email),
                        "phone_number": JSON.stringify(phoneNumber)
                   },
                    headers: {
                       "Content-type": "text/plain"
                   }
               }
           )
        } else {
            alert("Incorrect information")
        }

        setName("");
        setLastname("");
        setEmail("");
        setPhoneNumber("");
    }

    const validateName = (name) => {
        let nameA = name.split("");
        if(nameA.length > 1){
            return true
        } else {
            return false
        }
    }

    const validatePhoneNumber = (number) => {
        let numberA = number.split("");
        if(numberA.length > 10){
            return true
        } else {
            return false
        }
    }
    return (
        <div className="myform-styles" >
            <label>NAME</label>
            <input type="text" placeholder="Enter name" value={name} onChange={e => setName(e.target.value)} required />
            <label>LASTNAME</label>
            <input type="text" placeholder="Enter lastname" value={lastname} onChange={e => setLastname(e.target.value)} required />
            <label>EMAIL</label>
            <input type="text" placeholder="Enter email" value={email} onChange={e => setEmail(e.target.value)} required />
            <label>PHONE NUMBER</label>
            <input type="text" placeholder="Enter phone number" value={phoneNumber} onChange={e => setPhoneNumber(e.target.value)} required />
            
        </div>
        
    );
}

export default MyForm;



